import { GoogleAnalytics } from "@next/third-parties/google";
import type { Metadata } from "next";
import "./globals.css";
import { JetBrains_Mono } from "next/font/google";
import Header from "@/components/Header";
import Hero from "@/components/Hero";
import Footer from "@/components/Footer";
const jetBrainsMono = JetBrains_Mono({
  subsets: ["latin"],
  display: "swap",
  variable: "--font-jetbrains-mono",
});

export const metadata: Metadata = {
  title: "Bülent ALA - Personal Blog",
  description: "Bülent ALA - Personal Blog",
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" className={jetBrainsMono.variable}>
      <body className="font-mono text-sm tracking-tight antialiased">
        <div className="mx-auto grid min-h-screen max-w-2xl grid-cols-12 grid-rows-12 gap-4 p-4">
          <Header />
          <Hero />
          <main className="col-span-12 col-start-1 row-span-9 row-start-3">
            {children}
          </main>
          <Footer />
        </div>
      </body>
      <GoogleAnalytics gaId="G-51M62S5FLH" />
    </html>
  );
}

import PlayList from "@/components/PlayList";

export default function homePage() {
  return (
    <>
      <PlayList />
    </>
  );
}

import Test from "@/components/Test";
export default function TestPage() {
  return (
    <div>
      <Test />
    </div>
  );
}

export default function Footer() {
  return (
    <footer className="col-span-12 col-start-1 row-span-1 row-start-12 grid items-center text-xs">
      Bülent ALA
    </footer>
  );
}

import Logo from "@/components/Logo";

export default function Header() {
  return (
    <header className="col-span-12 col-start-1 row-span-1 row-start-1 grid items-center">
      <Logo />
    </header>
  );
}

export default function Hero() {
  return (
    <section className="col-span-12 col-start-1 row-span-1 row-start-2 grid items-center font-thin italic">
      May the light be with you.
    </section>
  );
}

import Link from "next/link";

export default function Logo() {
  return (
    <Link href={"/"} className="">
      A|A
    </Link>
  );
}

import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { YouTubeEmbed } from "@next/third-parties/google";

const playList = [
  {
    id: 1,
    title: "Denizdeyim",
    artist: "Peyk",
    songId: "zPLJX-Pqgus",
    description:
      "Saçların var yüzüne düşen. Saçların ki kıvır kıvır. İçiyoruz ağır ağır, Acele yok. Gereği yok...",
  },
  {
    id: 2,
    title: "Kocaman Sıfır",
    artist: "Peyk",
    songId: "O64W81QgYrg",
    description:
      "Ve de hep bu yanlış anlamam, bok gibi zamanlamam, çuvallamam Ve gelmişiz bugüne beni çözmeye...",
  },
  {
    id: 3,
    title: "Bipolar",
    artist: "Şara Kaplan",
    songId: "I_fDIOc17NM",
    description: "bipolarım bi geçimsizim...",
  },
  {
    id: 4,
    title: "Saraiman",
    artist: "Ladaniva",
    songId: "vGGbp88ziXU",
    description: "datdre datdre datdre day da da...",
  },
  {
    id: 5,
    title: "Jungle",
    artist: "Tash Sultana",
    songId: "Vn8phH0k5HI",
    description: "Ashes to ashes",
  },
];

export default function PlayList() {
  return (
    <div>
      <p className="pb-4 font-medium text-gray-500">playList</p>
      <div>
        {playList.map((song) => (
          <Dialog key={song.id}>
            <DialogTrigger className="grid w-full grid-cols-[_auto_1fr_auto] pb-2">
              <div className="col-start-1">{song.title}</div>
              <div className="col-start-2 mx-1 h-4 border-b-2 border-dotted">
                {""}
              </div>
              <div className="col-start-3">{song.artist}</div>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>
                  {song.title}-{song.artist}
                </DialogTitle>
                <DialogDescription className="text-xs">
                  {song.description}
                </DialogDescription>
              </DialogHeader>
              <YouTubeEmbed videoid={song.songId} params="controls=0" />
            </DialogContent>
          </Dialog>
        ))}
      </div>
    </div>
  );
}

